import "@styles/react/apps/app-users.scss";
import ClientDataTable from "./table";
import "./table.css";

import "@styles/react/libs/tables/react-dataTable-component.scss";
import React, { Fragment, useEffect, useState } from "react";
import "./table.css";
import { Col, Row } from "react-bootstrap";
import code_Qr from "../../Admin/assests/qr-code2.png";
import reportIcon from "../../Admin/assests/icons/report_icon.png";
import inspecPhoto from "../../Admin/assests/inspecPhoto.png";
import { useHistory, useLocation, useParams } from "react-router-dom";
import Spinerr from "../../views/components/spinners/SpinnerGrowing";
import axios from "axios";

import {
  UncontrolledButtonDropdown,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
} from "reactstrap";
import { MoreHorizontal, Trash, Edit2 } from "react-feather";
import { Button } from "reactstrap";
import { log } from "@craco/craco/lib/logger";

const iconSize = {
  width: "20px",
  height: "20px",
};

const inspe_photo = {
  width: "85px",
  height: "85px",
  borderRadius: "8px",
};

const JobDetail = () => {
  // get
  const [isLoading, setIsLoading] = useState(true);

  const { id } = useParams();
  const [dataRow, setDataRow] = useState({});

  function getData(id) {
    setIsLoading(true);
    axios
      .post(`${global.BASEURL}getRecords/jobs`)
      .then((res) => {
        const matchedData = res.data.data.find((item) => item._id === id);
        setDataRow(matchedData);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  }

  useEffect(() => {
    getData(id);
  }, [id]);

  let data = [
    {
      srno: "09",
      job_no: "7003",
      inpsection_date: "27-03-2023",
      rep: "Max Well",
      inspection_report: (
        <>
          <img style={iconSize} src={reportIcon} alt="" />
        </>
      ),
      photo: (
        <div>
          <img style={inspe_photo} src={inspecPhoto} alt="" />
        </div>
      ),
    },
  ];

  const columns = [
    {
      name: "Sr.no",
      selector: (row) => row.srno,
      sortable: "true",
      maxWidth: "2rem",
    },
    {
      name: "Job Number",
      selector: (row) => row.job_no,
      sortable: "true",
      // maxWidth: "2rem",
    },
    {
      name: "Inspection Date",
      selector: (row) => row.inpsection_date,
      sortable: "true",
      // maxWidth:"6rem"
    },
    {
      name: "Rep",
      selector: (row) => row.rep,
      sortable: "true",
      // maxWidth:"6rem"
    },
    {
      name: "Inspection Report",
      selector: (row) => row.inspection_report,
      sortable: "true",
      // maxWidth:"6rem"
    },
    {
      name: "Photo",
      selector: (row) => row.photo,
      sortable: "true",
      // maxWidth:"6rem"
    },
    {
      name: "Action",
      allowOverflow: true,
      maxWidth: "7rem",
      minWidth: "2rem",
      cell: () => {
        // modal edit

        return (
          <div className="d-flex justify-content-center w-100">
            <UncontrolledDropdown className="">
              <DropdownToggle className="pe-1 " tag="span">
                <MoreHorizontal size={15} />
              </DropdownToggle>
              <DropdownMenu end>
                <DropdownItem>
                  <div className="btn btn-white w-100 p-0 m-0">
                    <span className="align-middle ms-50">Edit</span>
                  </div>
                </DropdownItem>

                <DropdownItem>
                  <div className="btn btn-white w-100 p-0 m-0">
                    <span className="align-middle ms-50">Delete</span>
                  </div>
                </DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>
          </div>
        );
      },
    },
  ];

  // tabel secound data satrt
  let data2 = [
    {
      srno: "09",
      ass_rep: "7003",
      per_req: "Lorem",
      ppp_req: "Lorem",
      employee: "Max Well",
    },
  ];

  const columns2 = [
    {
      name: "Sr.no",
      selector: (row) => row.srno,
      sortable: "true",
      maxWidth: "2rem",
    },
    {
      name: "Assigned Rep",
      selector: (row) => row.ass_rep,
      sortable: "true",
      // maxWidth: "2rem",
    },
    {
      name: "Permits Required",
      selector: (row) => row.per_req,
      sortable: "true",
      // maxWidth:"6rem"
    },
    {
      name: "P.P.E Required",
